package com.example.movies.core.data.remoteimport com.google.gson.GsonBuilderimport okhttp3.OkHttpClientimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryobject Api {    private const val url = "10.152.0.180:3000"    private const val httpUrl = "http://$url/"    const val wsUrl = "ws://$url"    private var gson = GsonBuilder().create()    val tokenInterceptor = TokenInterceptor()    val okHttpClient = OkHttpClient.Builder().apply {        this.addInterceptor(tokenInterceptor)    }.build()    val retrofit: Retrofit =        Retrofit.Builder().baseUrl(httpUrl).addConverterFactory(GsonConverterFactory.create(gson))//        .client(okHttpClient) // does not work in android with the latest libs            .build()}